import React from "react";
import { css, styled } from "styled-components";

const Button = ({ children, size, variant = "outlined", ...rest }) => {
  if (variant === "oulined") {
    return "#442424";
  }
  return (
    <ButtonStyle size={size} variant={getVariant} {...rest}>
      {children}
    </ButtonStyle>
  );
};

export default Button;


const ButtonStyle=styled.button`
  padding: 8px 16px;
  border-radius: 4px;
  font-size: 16px;
  cursor: pointer;
  ${props =>
    props.variant === "outlined" &&
    css`
      color: #442424;
      border: 2px solid #442424;
      background-color: transparent;
    `}
`
