import React from "react";
import { css, styled } from "styled-components";

const Button = ({ children, size, variant = "outlined",disabled, ...rest }) => {
  return (
    <ButtonStyle size={size} variant={variant} {...rest} disabled={disabled}>
      {children}
    </ButtonStyle>
  );
};

export default Button;

const ButtonStyle = styled.button`
  padding: 8px 16px;
  border-radius: 4px;
  font-size: 16px;
  cursor: pointer;
  ${(props) =>
    props.variant === "outlined" &&
    css`
      color: #1F6ED4;
      border: 2px solid #1F6ED4;
      background-color: transparent;

      &:hover{
        border: 3px solid #4C94F0;
        color: #4C94F0;
      }
      &:active{
        border: 3px solid #0052BC;
        color: #ffff;
        background-color: #0052BC;
      }
      &:disabled{
        color: #C9C9C9;
        border: 1px solid #C9C9C9;
      }
    `}
`;
