import React from "react";
import { css, styled } from "styled-components";

const Button = ({ children, size, variant = "outlined", ...rest }) => {
  return (
    <ButtonStyle size={size} variant={variant} {...rest}>
      {children}
    </ButtonStyle>
  );
};

export default Button;

const ButtonStyle = styled.button`
  padding: 8px 16px;
  border-radius: 4px;
  font-size: 16px;
  cursor: pointer;
  ${(props) =>
    props.variant === "outlined" &&
    css`
      color: #116aa9;
      border: 2px solid #116aa9;
      background-color: transparent;

      &:hover{
        border: 3px solid #11a950;
      }
    `}
    ${(props) =>
    props.variant === "contained" &&
    css`
      color: #ffff;
      border: 2px solid #116aa9;
      background-color: #116aa9;
    `}
    ${(props) =>
    props.variant === "warning" &&
    css`
      color: #ffff;
      border: 2px solid #ffb508;
      background-color: #ffb508;
    `}
    ${(props) =>
    props.variant === "danger" &&
    css`
      color: #ffff;
      border: 2px solid #f34040;
      background-color: #f34040;
    `}
`;
