import React, { useState } from "react";
import { styled } from "styled-components";
import MovieList from "./MovieList";

const MovieItemList = ({ movieList, setMovieList }) => {  
  return (
    <MovieItem >
      {movieList.map((item) => (
        <MovieList
          title={item.name}
          image={item.img}
          movies={movieList}
          movie={item}
          rating={item.rating}
          key={item.id}
          setMovieList={setMovieList}
        />
      ))}
    </MovieItem>
  );
};

export default MovieItemList;

const MovieItemStyled = styled.div`
  margin: auto;
  display: flex;
  flex-direction: column;
  width: 700px;
  margin-top: 2rem;
  background-color: rgba(243, 243, 243, 0.891);
  border-radius: 20px;
`;
