import React, { useState } from "react";
import Modal from "./UI/Modal";
import Button from "./UI/Button";

const MovieList = ({ title, image, rating, movie, movies, setMovieList }) => {
  console.log(movies, "l");
  console.log(movie);
  const [isModalVisible, setModalVisible] = useState(false);
  const toggleModalHandler = () => {
    setModalVisible((prev) => !prev);
  };
  const deleteHandler = () => {
    setModalVisible(false);
    const updatedMovieList = movies.filter((item) => movie.id !== item.id);
    log
    setMovieList(updatedMovieList);
  };

  return (
    <>
      {isModalVisible ? (
        <Modal>
          <h3>Are you sure wanna delete?</h3>
          <Button color="black" onClick={toggleModalHandler}>
            No
          </Button>
          <Button color="blue" onClick={deleteHandler}>
            Yes
          </Button>
        </Modal>
      ) : null}
      <div>
        <div>
          <img src={image} alt="img" />
        </div>
        <div>
          <p>{title}</p>
        </div>
        <div>
          <div>{rating}/5</div>
          <Button color="red" onClick={toggleModalHandler}>
            Delete
          </Button>
        </div>
      </div>
    </>
  );
};

export default MovieList;
