import React, { useState } from "react";
import Modal from "./UI/Modal";
import Button from "./UI/Button";

const MovieList = ({ title, image, rating, movie, movies, deleteMovies }) => {
  const [isModalVisible, setModalVisible] = useState(false);
  const toggleModalHandler = () => {
    setModalVisible((prev) => !prev);
  };
  const deleteHandler = (id) => {
    const updatedMovieList = movies.filter((movie) => movie.id !== id);
    deleteMovies(updatedMovieList);
  };

  return (
    <>
      {isModalVisible ? (
        <Modal>
          <h3>Are you sure wanna delete?</h3>
          <Button color='black'>No</Button>
          <Button color="blue">Yes</Button>
        </Modal>
      ) : null}
      <div>
        <div>
          <img src={image} alt="img" />
        </div>
        <div>
          <p>{title}</p>
        </div>
        <div>
          <div>{rating}/5</div>
          <Button color="red" onClick={toggleModalHandler}>
            Delete
          </Button>
        </div>
      </div>
    </>
  );
};

export default MovieList;
