import { createRef, useState } from "react";
import Modal from "./UI/Modal";
import Input from "./UI/FormInput";
import Button from "./UI/Button";
import { styled } from "styled-components";

const Header = ({pushData}) => {
  const [openModal, setOpenModal] = useState(false);

  const nameMovie = createRef();
  const imageUrl = createRef();
  const ratingMovie = createRef();

  const closeCansel = () => {
    setOpenModal((prev) => !prev);
  };

  const addInfoHandler = (e) => {
    e.preventDefault();


    const name=nameMovie.current.value
    const image=imageUrl.current.value
    const rating=ratingMovie.current.value

    if(name && image && rating){
      const data={
        name,
        image,
        rating,
        id:Math.random().toString()
      } 
      pushData={}
    }
  };
  return (
    <header>
      <h2>Favorite Movies</h2>
      <button className="btn-add" onClick={closeCansel}>
        ADD MOVIE
      </button>

      {openModal ? (
        <Modal onClose={closeCansel}>
          <div>
            <Input labelName="Movie Title" />
            <Input labelName="Image URl" />
            <Input labelName="YourRating" />
          </div>

          <ButtonContainer>
            <Button color="aqua" onClick={closeCansel}>
              Cancel
            </Button>
            <Button color="blue">Add</Button>
          </ButtonContainer>
        </Modal>
      ) : null}
    </header>
  );
};
export default Header;

const ButtonContainer = styled.div`
  display: flex;
  justify-content: flex-end;
  gap: 0.5rem;
  padding-top: 1rem;
`;
